    private void bt_updateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bt_updateActionPerformed
        // TODO add your handling code here:
        try {
            // TODO add your handling code here:
            ps = (PreparedStatement) conn.prepareStatement("update db_data_siswa "
                    + "set nim = ?, nama = ?, jurusan = ?, alamat = ? "
                    + "where nim = ? and status_deleted = '0' ");
            ps.setString(1, tx_nim.getText());
            ps.setString(2, tx_nama.getText());
            ps.setString(3, (String) cb_jurusan.getSelectedItem());
            ps.setString(4, tx_alamat.getText());

            ps.setString(5, tx_nim.getText());

            if (ps.executeUpdate() == 1) {
                JOptionPane.showMessageDialog(null, "Data berhasil di update!");
                resetInput();
               tampilData(null);
            } else {
                JOptionPane.showMessageDialog(null, "Gagal update data", "Alert", JOptionPane.ERROR_MESSAGE);
            }
        } catch (SQLException ex) {
            Logger.getLogger(formMahasiswa.class.getName()).log(Level.SEVERE, null, ex);
        }


    }//GEN-LAST:event_bt_updateActionPerformed

    private void bt_insertActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bt_insertActionPerformed
        try {
            if (cekNIM(tx_nim.getText()) == true) {
                JOptionPane.showMessageDialog(null, "Nik sudah terdaftar !", "Alert", JOptionPane.ERROR_MESSAGE);
            } else {
                ps = (PreparedStatement) conn.prepareStatement("insert into db_data_siswa "
                        + "(nim, nama, jurusan, alamat)"
                        + "value (?,?,?,?)");

                ps.setString(1, tx_nim.getText());
                ps.setString(2, tx_nama.getText());
                ps.setString(3, (String) cb_jurusan.getSelectedItem());
                ps.setString(4, tx_alamat.getText());

                if (ps.executeUpdate() == 1) {
                    JOptionPane.showMessageDialog(null, "data berhasil disimpan.");
                    tampilData(null);
                    resetInput();
            
                } else {
                    JOptionPane.showMessageDialog(null, "Gagal simpan data1", "Alert", JOptionPane.ERROR_MESSAGE);
                }
            }



        } catch (SQLException ex) {
            Logger.getLogger(formMahasiswa.class.getName()).log(Level.SEVERE, null, ex);
        }




    }//GEN-LAST:event_bt_insertActionPerformed

    private void table_listMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_table_listMouseClicked
        // TODO add your handling code here:
        bt_update.setEnabled(true);
        bt_delete.setEnabled(true);
        bt_insert.setEnabled(false);

        String NIK = (String) table_list.getValueAt(table_list.getSelectedRow(), 0);
        try {
            st = (Statement) conn.createStatement();
            rs = st.executeQuery("Select * from db_data_siswa where status_deleted = '0' and nim = '" + NIK + "' ");
            rs.next();

            tx_nama.setText(rs.getString("nama"));
            tx_nim.setText(rs.getString("nim"));
            cb_jurusan.setSelectedItem(rs.getString("jurusan"));
            tx_alamat.setText(rs.getString("alamat"));

        } catch (SQLException ex) {
            Logger.getLogger(formMahasiswa.class.getName()).log(Level.SEVERE, null, ex);
        }

//        System.out.println(tbl_list_siswa.getValueAt(tbl_list_siswa.getSelectedRow(), 0));


    }//GEN-LAST:event_table_listMouseClicked

