    private void bt_deleteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bt_deleteActionPerformed
        // TODO add your handling code here:
        try {
            // TODO add your handling code here:
            String NIK = tx_nim.getText();
            ps = (PreparedStatement) conn.prepareStatement("delete from db_data_siswa where nim = ? ");
            ps.setString(1, NIK);

            if (ps.executeUpdate() == 1) {
                JOptionPane.showMessageDialog(null, "Data Berhasil di hapus!");
            } else {
                JOptionPane.showMessageDialog(null, "Gagal Hapus Data!", "Alert", JOptionPane.ERROR_MESSAGE);
            }

            resetInput();
            tampilData(null);
        } catch (SQLException ex) {
            Logger.getLogger(formMahasiswa.class.getName()).log(Level.SEVERE, null, ex);
        }

    }//GEN-LAST:event_bt_deleteActionPerformed

